SELECT co.courseofferid, c.courseid, co.year, co.quartile, c.coursename, c.coursedescription, c.degreeid, c.ects, d.dept, d.degreedescription, d.totalects, t.teacherid, t.teachername, t.address, t.birthyearteacher, t.gender from courseoffers co inner join courses c on co.courseid = c.courseid inner join degrees d on d.degreeid = c.degreeid inner join teacherassignmentstocourses ta on co.courseofferid = ta.courseofferid inner join teachers t on t.teacherid =ta.teacherid where co.courseofferid = 1;
SELECT co.courseofferid, co.courseid, co.year, co.quartile, s.studentid, s.studentname, s.address, s.birthyearstudent, s.gender, sr.degreeid, d.dept, d.degreedescription, d.totalects from studentassistants sa inner join courseoffers co on sa.courseofferid = co.courseofferid inner join studentregistrationstodegrees sr on sa.StudentRegistrationId = sr.StudentRegistrationId inner join students s on sr.studentid = s.studentid inner join degrees d on sr.degreeid = d.degreeid where sr.studentregistrationid = 140;
SELECT AVG(Grade) FROM CourseRegistrations WHERE StudentRegistrationId=140;
