SELECT co.courseofferid, co.courseid, co.year, co.quartile, c.coursename, c.coursedescription, c.degreeid, c.ects, d.dept, d.degreedescription, d.totalects, t.teacherid, t.teachername, t.address, t.birthyearteacher, t.gender from courseoffers co, courses c, degrees d, teacherassignmentstocourses ta, teachers t where co.courseofferid = 1 AND co.courseid = c.courseid AND c.degreeid = d.degreeid AND ta.teacherid = t.teacherid AND ta.courseofferid = co.courseofferid;
SELECT co.courseofferid, co.courseid, co.year, co.quartile, s.studentid, s.studentname, s.address, s.birthyearstudent, s.gender, d.degreeid, d.dept, d.degreedescription, d.totalects from studentassistants sa, courseoffers co, studentregistrationstodegrees sr, students s, degrees d where sr.studentregistrationid = 3 AND sa.courseofferid = co.courseofferid AND sr.degreeid = d.degreeid AND sr.studentid = s.studentid AND sa.studentregistrationid = sr.studentregistrationid;
SELECT AVG(grade) FROM courseregistrations WHERE studentregistrationid=3;
